mapper.select:
select 
	sum(r.login_count) login_count,
	DATE_FORMAT(insert_time,'%H') hour
 from  
 	login_result r
 left join 
 	t_account_detail d
 on 
 	r.user_id = d.iAccountId 	 	


		and r.user_id =#{user_id}


		and r.insert_time >= #{start_time}


		and r.insert_time <=#{end_time}


	    and d.sUserName=#{user_name}


		and d.sPhone=#{mobile}


group by DATE_FORMAT(r.insert_time,'%H')

select count(*) from (select id from login_result r
	left join t_account_detail d
	on r.user_id = d.iAccountId 


		and r.user_id =#{user_id}


		and r.insert_time >= #{start_time}


		and r.insert_time <=#{end_time}


	    and d.sUserName=#{user_name}


		and d.sPhone=#{mobile}


group by DATE_FORMAT(r.insert_time,'%H')) t
